import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useFormik } from 'formik';
import { useNavigate, Link } from 'react-router-dom';

function AdminRegister() {
  const [adminUsers, setAdminUsers] = useState([]);
  const [msg, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:8080/getadmin').then(res => {
      setAdminUsers(res.data);
    });
  }, []);

  const formik = useFormik({
    initialValues: {
      UserId: '',
      FirstName: '',
      LastName: '',
      Password: ''
    },
    onSubmit: (values) => {
  console.log('Submitting form with values:', values); // Debug form data
  axios
    .post('http://127.0.0.1:8080/adminregister', values)
    .then((response) => {
      console.log('API response:', response.data); // Debug API response
      alert('Registered Successfully');
      navigate('/adminlogin'); // Use navigate (from previous fix)
    })
    .catch((error) => {
      console.error('Error during registration:', error); // Log error
      alert('Registration failed: ' + error.message);
    });
}
  });

  function verifyUserId(e) {
    const taken = adminUsers.some(user => user.UserId === e.target.value);
    setMessage(taken ? 'User Name Taken : Try Another' : 'User Name Available');
  }

  return (
    <div className="w-50">
      <form onSubmit={formik.handleSubmit}>
  <h3>Admin Register</h3>
  <div>
    <dt>User Id</dt>
    <dd>
      <input
        type="text"
        onKeyUp={verifyUserId}
        onChange={formik.handleChange}
        name="UserId"
        value={formik.values.UserId}
      />
    </dd>
    <dd>{msg}</dd>
    <dt>First Name</dt>
    <dd>
      <input
        type="text"
        onChange={formik.handleChange}
        name="FirstName"
        value={formik.values.FirstName}
      />
    </dd>
    <dt>Last Name</dt>
    <dd>
      <input
        type="text"
        onChange={formik.handleChange}
        name="LastName"
        value={formik.values.LastName}
      />
    </dd>
    <dt>Password</dt>
    <dd>
      <input
        type="password"
        onChange={formik.handleChange}
        name="Password"
        value={formik.values.Password}
      />
    </dd>
    <button type="submit" className="btn btn-primary">
      Register
    </button>
    <div className="mt-2">
      <Link className="btn btn-link" to="/adminlogin">
        Login
      </Link>
    </div>
  </div>
</form>
    </div>
  );
}

export default AdminRegister;
