import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import { useCookies } from 'react-cookie';

const LoginComponent = () => {
  const [users, setUsers] = useState([]);
  const [msg, setMsg] = useState('');
  const [userDetails, setUserDetails] = useState({ UserId: '', Password: '' });
  const history = useHistory();
  const [cookies, setCookie] = useCookies(['userid']);

  useEffect(() => {
    axios.get('http://127.0.0.1:8080/getadmin').then((res) => {
      setUsers(res.data);
    });
  }, []);

  const handleUserId = (e) => {
    setUserDetails({ ...userDetails, UserId: e.target.value });
  };

  const handlePassword = (e) => {
    setUserDetails({ ...userDetails, Password: e.target.value });
  };

  const handleLogin = () => {
    for (let user of users) {
      if (user.UserId === userDetails.UserId && user.Password === userDetails.Password) {
        setCookie('userid', userDetails.UserId, { path: '/' });
        history.push('/admindashboard');
        return;
      }
    }
    setMsg('Invalid Credentials - Try Again');
  };

  return (
    <div className="w-50">
      <h2>Admin Login</h2>
      <dl>
        <dt>User Id</dt>
        <dd>
          <input type="text" onChange={handleUserId} />
        </dd>
        <dt>Password</dt>
        <dd>
          <input type="password" onChange={handlePassword} />
        </dd>
      </dl>
      <button onClick={handleLogin} className="btn btn-primary">
        Login
      </button>
      <h3>{msg}</h3>
      <dl>
        New User? <Link to="/adminregister">Register</Link>
      </dl>
    </div>
  );
};

export default LoginComponent;
