import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useCookies } from 'react-cookie';
import { useNavigate, Link } from 'react-router-dom';

function AdminLogin() {
  const [users, setUsers] = useState([]);
  const [msg, setMsg] = useState('');
  const [credentials, setCredentials] = useState({ UserId: '', Password: '' });
  const [cookies, setCookie] = useCookies(['userid']);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:8080/getadmin').then(res => {
      setUsers(res.data);
    });
  }, []);

  const handleLogin = () => {
    const match = users.find(user => user.UserId === credentials.UserId && user.Password === credentials.Password);
    if (match) {
      setCookie('userid', credentials.UserId, { path: '/' });
      navigate('/admindashboard');
    } else {
      setMsg('Invalid Credentials - Try Again');
    }
  };

  return (
    <div className="w-50">
      <h2>Admin Login</h2>
      <dl>
        <dt>User Id</dt>
        <dd><input type="text" className="form-control" onChange={e => setCredentials({ ...credentials, UserId: e.target.value })} /></dd>
        <dt>Password</dt>
        <dd><input type="password" className="form-control" onChange={e => setCredentials({ ...credentials, Password: e.target.value })} /></dd>
      </dl>
      <button className="btn btn-primary" onClick={handleLogin}>Login</button>
      <h4 className="text-danger">{msg}</h4>
      <div>
        New User? <Link to="/adminregister">Register</Link>
      </div>
    </div>
  );
}

export default AdminLogin;
